@RestController
@RequestMapping(/api)
class BlogController {
 @Autowired
 private UserRepository userRepository;
 @Autowired
 private PostRepository postRepository;
 @Autowired
 private BCryptPasswordEncoder passwordEncoder;
 @PostMapping(/register)
 public ResponseEntity<String> register(@RequestBody User user) {
  if (userRepository.findByUsername(user.getUsername()).isPresent()) {
   return new ResponseEntity<>(User already exists, HttpStatus.BAD_REQUEST);
  }
  user.setPassword(passwordEncoder.encode(user.getPassword()));
  userRepository.save(user);
  return new ResponseEntity<>(User registered successfully, HttpStatus.CREATED);
 }
 @PostMapping(/login)
 public ResponseEntity<String> login(@RequestBody User user) {
  Optional<User> existingUser = userRepository.findByUsername(user.getUsername());
  if (existingUser.isPresent() && passwordEncoder.matches(user.getPassword(), existingUser.get().getPassword())) {
   return new ResponseEntity<>(Login successful, HttpStatus.OK);
  }
  return new ResponseEntity<>(Invalid credentials, HttpStatus.UNAUTHORIZED);
 }
 @GetMapping(/users)
 public ResponseEntity<List<User>> getUsers() {
  List<User> users = userRepository.findAll();
  return new ResponseEntity<>(users, HttpStatus.OK);
 }
 @GetMapping(/user/{username})
 public ResponseEntity<User> getUser(@PathVariable String username) {
  Optional<User> user = userRepository.findByUsername(username);
  return user.map(ResponseEntity::ok).orElseGet(() -> new ResponseEntity<>(HttpStatus.NOT_FOUND));
 }
 @DeleteMapping(/user/{username})
 public ResponseEntity<String> deleteUser(@PathVariable String username) {
  Optional<User> user = userRepository.findByUsername(username);
  if (user.isPresent()) {
   userRepository.delete(user.get());
   return new ResponseEntity<>(User deleted successfully, HttpStatus.OK);
  }
  return new ResponseEntity<>(User not found, HttpStatus.NOT_FOUND);
 }
 @PostMapping(/createPost)
 public ResponseEntity<String> createPost(@RequestBody Post post) {
  postRepository.save(post);
  return new ResponseEntity<>(Post created successfully, HttpStatus.CREATED);
 }
 @GetMapping(/posts)
 public ResponseEntity<List<Post>> getPosts() {
  List<Post> posts = postRepository.findAll();
  return new ResponseEntity<>(posts, HttpStatus.OK);
 }
 @DeleteMapping(/post/{id})
 public ResponseEntity<String> deletePost(@PathVariable Long id) {
  if (postRepository.existsById(id)) {
   postRepository.deleteById(id);
   return new ResponseEntity<>(Post deleted successfully, HttpStatus.OK);
  }
  return new ResponseEntity<>(Post not found, HttpStatus.NOT_FOUND);
 }
 @PutMapping(/updatePost/{id})
 public ResponseEntity<String> updatePost(@PathVariable Long id, @RequestBody Post updatedPost) {
  if (postRepository.existsById(id)) {
   Post post = postRepository.findById(id).get();
   post.setTitle(updatedPost.getTitle());
   post.setContent(updatedPost.getContent());
   postRepository.save(post);
   return new ResponseEntity<>(Post updated successfully, HttpStatus.OK);
  }
  return new ResponseEntity<>(Post not found, HttpStatus.NOT_FOUND);
 }
 @GetMapping(/searchPosts/{keyword})
 public ResponseEntity<List<Post>> searchPosts(@PathVariable String keyword) {
  List<Post> foundPosts = new ArrayList<>();
  for (Post post : postRepository.findAll()) {
   if (post.getTitle().contains(keyword) || post.getContent().contains(keyword)) {
    foundPosts.add(post);
   }
  }
  return new ResponseEntity<>(foundPosts, HttpStatus.OK);
 }
}

