sub update_user {
 my $id = $cgi->param(id);
 my $username = $cgi->param(username);
 my $email = $cgi->param(email);
 eval {
  my $sth = $dbh->prepare(UPDATE users SET username = ?, email = ? WHERE id = ?);
  $sth->execute($username, $email, $id);
 };
 if ($@) {
  print $cgi->p(User not found);
 } else {
  print $cgi->p(User updated successfully);
 }
}
sub view_user {
 my $id = $cgi->param(id);
 my $sth = $dbh->prepare(SELECT * FROM users WHERE id = ?);
 $sth->execute($id);
 my $user = $sth->fetchrow_hashref;
 if ($user) {
  print $cgi->h2(User Profile);
  print $cgi->p(Username: $user->{username});
  print $cgi->p(Email: $user->{email});
  print $cgi->p(Created at: $user->{created_at});
 } else {
  print $cgi->p(User not found);
 }
}
